import React, { Component } from 'react';
// axios for handling api
import axios from 'axios';

// LIST RENDERING DENGAN DATA DARI API
function search (input) {
  return function (x) {

  }
}

class App extends Component {
  constructor() {
    super()
    this.state = {
      planets : [],
      input : ''
    }
    this._searchHandler = this._searchHandler.bind(this)
  }

  componentDidMount() {
    axios.get("https://swapi.co/api/people/")
      .then((res) => {
        this.setState({ planets : res.data.results })
      })
      .catch((err) => console.log(err))
  }    

  _searchHandler (event) {
    this.setState({ input : event.target.value })
  }



  render() {
    return (
      <div>
        {/* <button onClick = {this._handleClick}>GET PEOPLES</button> */}
        <h1>List Data Peoples From Starwars API (swapi.co)</h1>
        <input 
          type="text" 
          placeholder="Pencarian..."
          onChange = {this._searchHandler}
        />
        <table>
          <thead>
            <th>Nama</th>
            <th>Height</th>
            <th>Mass</th>
            <th>Gender</th>
          </thead>
          <tbody>
            {
              this.state.planets.map((data, index) => {
                return (
                  <tr key = {index}>
                    <td>{ data.name }</td>
                    <td>{ data.height }</td>
                    <td>{ data.mass }</td>
                    <td>{ data.gender }</td>
                  </tr>
                )
              })
            }
          </tbody>
        </table>

      </div>
    )
  }
}

export default App;


